// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ColdChain {
    struct Reading {
        uint256 timestamp;
        int256 temperature;
        int256 humidity;
    }

    Reading[] public readings;

    // Define thresholds for temperature (customize as needed)
    int256 public constant MIN_TEMPERATURE = 2;
    int256 public constant MAX_TEMPERATURE = 8;

    // Event emitted when a new reading is recorded
    event NewReading(
        uint256 timestamp,
        int256 temperature,
        int256 humidity,
        bool compliant
    );

    // Function to record a new reading
    function recordReading(int256 _temperature, int256 _humidity) public {
        bool compliant = (_temperature >= MIN_TEMPERATURE &&
            _temperature <= MAX_TEMPERATURE);
        readings.push(Reading(block.timestamp, _temperature, _humidity));
        emit NewReading(block.timestamp, _temperature, _humidity, compliant);
    }
}
